select source.person_id as asource, target.person_id as atarget,count(*) counter,source.person,target.personfromresearch_bug_comment_people target,research_bug_comment_people source,research_comment_comment cowhere target.person = co.assigned_id andsource.person = co.author_idand co.author_id not in (select assigned_id from research_bug_fixer_people)group by asource, atargetorder by counter desc 