select a.bug_id, a.bug_status, a.resolution,  a.assigned_id, b.author_id,a.short_desc, a.delta_ts, a.created_ts, a.actual_time,b.created_ts as commentTS, b.the_text, a.reporter_id, a.qa_id, c.releaseId from bug a, comment b, research_release cwhere a.bug_id=b.bug_id anda.bug_status = 'RESOLVED' and a.resolution='FIXED'and b.created_ts <= c.releaseDate andb.created_ts > c.previousReleaseDateand the_text not like ('Created an attachment%')order by b.created_ts, c.releaseDate, a.bug_id-- , b.created_ts-- First specific bug explored below...-- and a.bug_id='f491424c-c0a8-0169-2a8c-eeeab06613b4'-- Another specific bug with a shorter comment string-- and a.bug_id='f48c27a6-c0a8-0169-2a8c-eeead87ab83d'